import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Import your existing components
import Navbar from './Components/modern_navbar.js';
import ConferencePage from './Components/ConferencePage.js';
import TeamPage from './Components/TeamPage.js';
import GamePage from './Components/GamePage.js';
import TriviaGame from './Components/TriviaGame.js';
import CommunityChat from './Components/CommunityChat.js';
import Registration from './Components/Registration.js';
import SubscriptionModal from './Components/SubscriptionModal.js';
import BroadcastBooth from './Components/BroadcastBooth.js';

function App() {
  const [user, setUser] = useState(null);
  const [showRegistration, setShowRegistration] = useState(false);
  const [showSubscription, setShowSubscription] = useState(false);
  const [showSignIn, setShowSignIn] = useState(false);

  // Mock Auth for now - no imports needed
  const mockAuth = {
    getCurrentUser: async () => {
      // Check if user data exists in memory or sessionStorage
      const savedUser = sessionStorage.getItem('cfb_user');
      if (savedUser) {
        return JSON.parse(savedUser);
      }
      throw new Error('No current user');
    },
    signIn: async (email, password) => {
      // Mock sign in - in real app, this would call your API
      const mockUser = {
        email: email,
        name: email.split('@')[0],
        subscription: 'premium',
        favoriteTeam: 'Arizona'
      };
      sessionStorage.setItem('cfb_user', JSON.stringify(mockUser));
      return mockUser;
    }
  };

  useEffect(() => {
    // Check for existing user session
    const checkUser = async () => {
      try {
        const currentUser = await mockAuth.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.log('No user session found');
      }
    };
    checkUser();
  }, []);

  // Sign In Modal Component
  const SignInModal = ({ isOpen, onClose, onSignInSuccess }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSignIn = async (e) => {
      e.preventDefault();
      setLoading(true);
      setError('');

      try {
        const user = await mockAuth.signIn(email, password);
        setUser(user);
        onSignInSuccess(user);
        onClose();
      } catch (err) {
        setError('Invalid credentials. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    if (!isOpen) return null;

    return (
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={e => e.stopPropagation()}>
          <div className="modal-header">
            <h2>Welcome Back!</h2>
            <button className="modal-close" onClick={onClose}>×</button>
          </div>
          
          <form onSubmit={handleSignIn} className="signin-form">
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                placeholder="Enter your email"
              />
            </div>
            
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="Enter your password"
              />
            </div>

            {error && <div className="error-message">{error}</div>}
            
            <button type="submit" className="btn-primary-full" disabled={loading}>
              {loading ? 'Signing In...' : 'Sign In'}
            </button>
          </form>

          <div className="modal-footer">
            <p>Don't have an account? 
              <button 
                className="link-button" 
                onClick={() => {
                  onClose();
                  setShowRegistration(true);
                }}
              >
                Start Free Trial
              </button>
            </p>
          </div>
        </div>
      </div>
    );
  };

  // HomePage Component with Broadcast Booth
  const HomePage = () => {
    const [currentStatIndex, setCurrentStatIndex] = useState(0);
    
    const rotatingStats = user ? [
      { number: "92.3%", label: "Your Accuracy Rate", subtext: "Welcome back!" },
      { number: "47", label: "Games This Week", subtext: "Ready for predictions" },
      { number: "Arizona", label: "Your Team", subtext: "Next game in 3 days" },
      { number: "Premium", label: "Account Status", subtext: "All features unlocked" },
      { number: "127%", label: "Your ROI", subtext: "Keep winning!" }
    ] : [
      { number: "92.3%", label: "Prediction Accuracy", subtext: "24% better than ESPN" },
      { number: "320,000+", label: "Data Points Analyzed", subtext: "Every game, every play" },
      { number: "544", label: "Teams Tracked", subtext: "Power 4 to FCS coverage" },
      { number: "54", label: "Prediction Factors", subtext: "Weather, injuries, momentum" },
      { number: "30-Day", label: "Free Trial", subtext: "No credit card required" }
    ];

    useEffect(() => {
      const interval = setInterval(() => {
        setCurrentStatIndex((prev) => (prev + 1) % rotatingStats.length);
      }, 3000);
      return () => clearInterval(interval);
    }, [rotatingStats.length, user]);

    return (
      <div className="homepage-container">
        {/* Hero Section with Rotating Stats */}
        <div className="hero-section">
          <div className="hero-content">
            <div className="hero-text">
              <h1 className="hero-title">
                {user ? 
                  `🏆 Welcome Back, ${user.name || user.email.split('@')[0]}!` : 
                  '🏆 The Most Accurate College Sports Predictions'
                }
              </h1>
              <p className="hero-subtitle">
                {user ? 
                  'Your personalized college sports analytics dashboard with 92.3% accurate predictions' :
                  'AI-powered analytics for Football & Basketball that consistently outperform ESPN, Vegas, and every major sports network'
                }
              </p>
              
              {/* Rotating Stats Display */}
              <div className="rotating-stat-card">
                <div className="stat-number">
                  {rotatingStats[currentStatIndex].number}
                </div>
                <div className="stat-label">
                  {rotatingStats[currentStatIndex].label}
                </div>
                <div className="stat-subtext">
                  {rotatingStats[currentStatIndex].subtext}
                </div>
              </div>

              <div className="hero-buttons">
                {user ? (
                  <>
                    <button 
                      className="cta-primary"
                      onClick={() => setShowSubscription(true)}
                    >
                      View My Predictions
                    </button>
                    <button 
                      className="cta-secondary"
                      onClick={() => window.location.href = '/conference/pac-12'}
                    >
                      Check {user.favoriteTeam || 'My Team'}
                    </button>
                  </>
                ) : (
                  <>
                    <button 
                      className="cta-primary"
                      onClick={() => setShowRegistration(true)}
                    >
                      Start Free 30-Day Trial
                    </button>
                    <button 
                      className="cta-secondary"
                      onClick={() => setShowSubscription(true)}
                    >
                      View Pricing Plans
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Broadcast Booth Integration */}
            <div className="hero-broadcast">
              <BroadcastBooth />
            </div>
          </div>
        </div>

        {/* Key Features Section */}
        <div className="features-section">
          <div className="container">
            <h2 className="section-title">Why Our Predictions Dominate College Sports</h2>
            <div className="features-grid">
              <div className="feature-card">
                <div className="feature-icon">🎯</div>
                <h3>92.3% Accuracy Rate</h3>
                <p>Our AI consistently outperforms ESPN (68.9%), CBS Sports (71.2%), and Vegas oddsmakers (74.6%) across football and basketball</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">⚡</div>
                <h3>Multi-Sport Analysis</h3>
                <p>Football now, Basketball in September. 54+ factors including weather, injuries, coaching decisions, and player momentum</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">🎬</div>
                <h3>Live Avatar Analysis</h3>
                <p>AI-generated sports analysts provide personalized breakdowns for every game and team you follow across all sports</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">📊</div>
                <h3>320K+ Data Points</h3>
                <p>Every play, every game, every season analyzed with machine learning algorithms that improve weekly</p>
              </div>
            </div>
          </div>
        </div>

        {/* Social Proof Section */}
        <div className="social-proof-section">
          <div className="container">
            <div className="proof-content">
              <h2>Join 2,847+ Winning Sports Bettors</h2>
              <p className="proof-subtitle">
                Our users have reported an average ROI of 127% using our college sports predictions
              </p>
              
              <div className="testimonials-grid">
                <div className="testimonial">
                  <div className="testimonial-text">
                    "Made back my annual subscription in the first weekend. This isn't just predictions - it's a money printer."
                  </div>
                  <div className="testimonial-author">- Jake M., Arizona State University</div>
                </div>
                <div className="testimonial">
                  <div className="testimonial-text">
                    "Finally, predictions that actually work. 92.3% accuracy isn't luck - it's science."
                  </div>
                  <div className="testimonial-author">- Sarah T., Professional Bettor</div>
                </div>
                <div className="testimonial">
                  <div className="testimonial-text">
                    "The avatar analysis videos are incredible. It's like having a personal sports analyst."
                  </div>
                  <div className="testimonial-author">- Mike R., Sports Enthusiast</div>
                </div>
              </div>

              <div className="final-cta">
                <button 
                  className="cta-primary-large"
                  onClick={() => setShowRegistration(true)}
                >
                  Get 30 Days Free - No Credit Card Required
                </button>
                <p className="cta-disclaimer">
                  Join before the early bird pricing ends. Cancel anytime.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <Router>
      <div className="App">
        <Navbar 
          user={user} 
          onShowRegistration={() => setShowRegistration(true)}
          onShowSubscription={() => setShowSubscription(true)}
          onShowSignIn={() => setShowSignIn(true)}
        />
        
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/conference/:conference" element={<ConferencePage user={user} onShowRegistration={() => setShowRegistration(true)} onShowSignIn={() => setShowSignIn(true)} />} />
          <Route path="/team/:teamName" element={<TeamPage user={user} onShowRegistration={() => setShowRegistration(true)} onShowSignIn={() => setShowSignIn(true)} />} />
          <Route path="/game/:gameId" element={<GamePage user={user} />} />
          <Route path="/trivia" element={<TriviaGame user={user} />} />
          <Route path="/community" element={<CommunityChat user={user} />} />
        </Routes>

        {/* Sign In Modal */}
        {showSignIn && (
          <SignInModal
            isOpen={showSignIn}
            onClose={() => setShowSignIn(false)}
            onSignInSuccess={(userData) => {
              setUser(userData);
              setShowSignIn(false);
            }}
          />
        )}

        {/* Registration Modal */}
        {showRegistration && (
          <Registration
            isOpen={showRegistration}
            onClose={() => setShowRegistration(false)}
            onRegistrationComplete={(userData) => {
              setUser(userData);
              setShowRegistration(false);
              setShowSubscription(true);
            }}
          />
        )}

        {/* Subscription Modal */}
        {showSubscription && (
          <SubscriptionModal
            isOpen={showSubscription}
            onClose={() => setShowSubscription(false)}
            user={user}
            onSubscriptionComplete={() => {
              console.log('Subscription successful!');
              setShowSubscription(false);
            }}
          />
        )}
      </div>
    </Router>
  );
}

export default App;
