import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import './ConferencePage.css';

const ConferencePage = ({ user, onShowRegistration, onShowSignIn }) => {
  const { conference } = useParams();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedConference, setSelectedConference] = useState(conference);

  // Real College Football Conference Data
  const conferenceData = {
    'sec': {
      name: 'SEC',
      fullName: 'Southeastern Conference',
      teams: [
        { name: 'Alabama', record: '12-2', ranking: 4, nextGame: 'vs Georgia (Jan 20)', city: 'Tuscaloosa, AL' },
        { name: 'Georgia', record: '11-3', ranking: 6, nextGame: 'vs Alabama (Jan 20)', city: 'Athens, GA' },
        { name: 'Texas', record: '13-1', ranking: 2, nextGame: 'vs Notre Dame (Jan 18)', city: 'Austin, TX' },
        { name: 'Tennessee', record: '10-3', ranking: 12, nextGame: 'vs Florida (Jan 15)', city: 'Knoxville, TN' },
        { name: 'Ole Miss', record: '9-4', ranking: 18, nextGame: 'vs Arkansas (Jan 16)', city: 'Oxford, MS' },
        { name: 'South Carolina', record: '9-4', ranking: 19, nextGame: 'vs Missouri (Jan 17)', city: 'Columbia, SC' },
        { name: 'LSU', record: '8-5', ranking: 25, nextGame: 'vs Vanderbilt (Jan 19)', city: 'Baton Rouge, LA' },
        { name: 'Missouri', record: '8-5', ranking: 'NR', nextGame: 'vs South Carolina (Jan 17)', city: 'Columbia, MO' },
        { name: 'Florida', record: '7-6', ranking: 'NR', nextGame: 'vs Tennessee (Jan 15)', city: 'Gainesville, FL' },
        { name: 'Arkansas', record: '6-7', ranking: 'NR', nextGame: 'vs Ole Miss (Jan 16)', city: 'Fayetteville, AR' },
        { name: 'Kentucky', record: '4-8', ranking: 'NR', nextGame: 'vs Auburn (Jan 21)', city: 'Lexington, KY' },
        { name: 'Auburn', record: '5-7', ranking: 'NR', nextGame: 'vs Kentucky (Jan 21)', city: 'Auburn, AL' },
        { name: 'Texas A&M', record: '8-5', ranking: 22, nextGame: 'vs Mississippi State (Jan 14)', city: 'College Station, TX' },
        { name: 'Mississippi State', record: '2-10', ranking: 'NR', nextGame: 'vs Texas A&M (Jan 14)', city: 'Starkville, MS' },
        { name: 'Vanderbilt', record: '6-7', ranking: 'NR', nextGame: 'vs LSU (Jan 19)', city: 'Nashville, TN' },
        { name: 'Oklahoma', record: '6-7', ranking: 'NR', nextGame: 'Season Complete', city: 'Norman, OK' }
      ]
    },
    'big-ten': {
      name: 'Big Ten',
      fullName: 'Big Ten Conference',
      teams: [
        { name: 'Oregon', record: '13-1', ranking: 1, nextGame: 'vs Penn State (Jan 20)', city: 'Eugene, OR' },
        { name: 'Penn State', record: '13-2', ranking: 3, nextGame: 'vs Oregon (Jan 20)', city: 'University Park, PA' },
        { name: 'Indiana', record: '11-2', ranking: 8, nextGame: 'vs Ohio State (Jan 18)', city: 'Bloomington, IN' },
        { name: 'Ohio State', record: '12-2', ranking: 5, nextGame: 'vs Indiana (Jan 18)', city: 'Columbus, OH' },
        { name: 'Michigan', record: '7-6', ranking: 'NR', nextGame: 'vs Wisconsin (Jan 16)', city: 'Ann Arbor, MI' },
        { name: 'Iowa', record: '8-5', ranking: 24, nextGame: 'vs Nebraska (Jan 17)', city: 'Iowa City, IA' },
        { name: 'Illinois', record: '9-4', ranking: 20, nextGame: 'vs Northwestern (Jan 15)', city: 'Champaign, IL' },
        { name: 'Wisconsin', record: '5-7', ranking: 'NR', nextGame: 'vs Michigan (Jan 16)', city: 'Madison, WI' },
        { name: 'Minnesota', record: '7-6', ranking: 'NR', nextGame: 'vs Rutgers (Jan 19)', city: 'Minneapolis, MN' },
        { name: 'Nebraska', record: '6-6', ranking: 'NR', nextGame: 'vs Iowa (Jan 17)', city: 'Lincoln, NE' },
        { name: 'Rutgers', record: '7-6', ranking: 'NR', nextGame: 'vs Minnesota (Jan 19)', city: 'Piscataway, NJ' },
        { name: 'Maryland', record: '4-8', ranking: 'NR', nextGame: 'vs Purdue (Jan 21)', city: 'College Park, MD' },
        { name: 'Michigan State', record: '5-7', ranking: 'NR', nextGame: 'Season Complete', city: 'East Lansing, MI' },
        { name: 'Northwestern', record: '4-8', ranking: 'NR', nextGame: 'vs Illinois (Jan 15)', city: 'Evanston, IL' },
        { name: 'Purdue', record: '1-11', ranking: 'NR', nextGame: 'vs Maryland (Jan 21)', city: 'West Lafayette, IN' },
        { name: 'UCLA', record: '5-7', ranking: 'NR', nextGame: 'Season Complete', city: 'Los Angeles, CA' },
        { name: 'USC', record: '6-7', ranking: 'NR', nextGame: 'Season Complete', city: 'Los Angeles, CA' },
        { name: 'Washington', record: '6-7', ranking: 'NR', nextGame: 'Season Complete', city: 'Seattle, WA' }
      ]
    },
    'big-12': {
      name: 'Big 12',
      fullName: 'Big 12 Conference',
      teams: [
        { name: 'Arizona State', record: '11-3', ranking: 11, nextGame: 'vs Boise State (Jan 19)', city: 'Tempe, AZ' },
        { name: 'Iowa State', record: '11-3', ranking: 13, nextGame: 'vs TCU (Jan 17)', city: 'Ames, IA' },
        { name: 'BYU', record: '10-3', ranking: 16, nextGame: 'vs Kansas State (Jan 15)', city: 'Provo, UT' },
        { name: 'Colorado', record: '9-4', ranking: 21, nextGame: 'vs Arizona (Jan 18)', city: 'Boulder, CO' },
        { name: 'Kansas State', record: '8-5', ranking: 'NR', nextGame: 'vs BYU (Jan 15)', city: 'Manhattan, KS' },
        { name: 'Arizona', record: '8-5', ranking: 23, nextGame: 'vs Colorado (Jan 18)', city: 'Tucson, AZ' },
        { name: 'TCU', record: '8-5', ranking: 'NR', nextGame: 'vs Iowa State (Jan 17)', city: 'Fort Worth, TX' },
        { name: 'Texas Tech', record: '8-5', ranking: 'NR', nextGame: 'vs West Virginia (Jan 16)', city: 'Lubbock, TX' },
        { name: 'West Virginia', record: '6-6', ranking: 'NR', nextGame: 'vs Texas Tech (Jan 16)', city: 'Morgantown, WV' },
        { name: 'Cincinnati', record: '5-7', ranking: 'NR', nextGame: 'vs UCF (Jan 20)', city: 'Cincinnati, OH' },
        { name: 'Baylor', record: '4-8', ranking: 'NR', nextGame: 'vs Houston (Jan 21)', city: 'Waco, TX' },
        { name: 'Houston', record: '4-8', ranking: 'NR', nextGame: 'vs Baylor (Jan 21)', city: 'Houston, TX' },
        { name: 'Kansas', record: '5-7', ranking: 'NR', nextGame: 'vs Oklahoma State (Jan 14)', city: 'Lawrence, KS' },
        { name: 'Oklahoma State', record: '3-9', ranking: 'NR', nextGame: 'vs Kansas (Jan 14)', city: 'Stillwater, OK' },
        { name: 'UCF', record: '4-8', ranking: 'NR', nextGame: 'vs Cincinnati (Jan 20)', city: 'Orlando, FL' },
        { name: 'Utah', record: '5-7', ranking: 'NR', nextGame: 'Season Complete', city: 'Salt Lake City, UT' }
      ]
    },
    'acc': {
      name: 'ACC',
      fullName: 'Atlantic Coast Conference',
      teams: [
        { name: 'SMU', record: '11-3', ranking: 9, nextGame: 'vs Clemson (Jan 18)', city: 'Dallas, TX' },
        { name: 'Clemson', record: '10-4', ranking: 15, nextGame: 'vs SMU (Jan 18)', city: 'Clemson, SC' },
        { name: 'Miami', record: '10-3', ranking: 14, nextGame: 'vs Virginia Tech (Jan 16)', city: 'Miami, FL' },
        { name: 'Louisville', record: '9-4', ranking: 17, nextGame: 'vs Duke (Jan 17)', city: 'Louisville, KY' },
        { name: 'Duke', record: '9-4', ranking: 'NR', nextGame: 'vs Louisville (Jan 17)', city: 'Durham, NC' },
        { name: 'Virginia Tech', record: '6-7', ranking: 'NR', nextGame: 'vs Miami (Jan 16)', city: 'Blacksburg, VA' },
        { name: 'Syracuse', record: '10-3', ranking: 'NR', nextGame: 'vs Pittsburgh (Jan 15)', city: 'Syracuse, NY' },
        { name: 'Pittsburgh', record: '7-6', ranking: 'NR', nextGame: 'vs Syracuse (Jan 15)', city: 'Pittsburgh, PA' },
        { name: 'North Carolina', record: '6-7', ranking: 'NR', nextGame: 'vs NC State (Jan 19)', city: 'Chapel Hill, NC' },
        { name: 'NC State', record: '5-7', ranking: 'NR', nextGame: 'vs North Carolina (Jan 19)', city: 'Raleigh, NC' },
        { name: 'Virginia', record: '5-7', ranking: 'NR', nextGame: 'vs Wake Forest (Jan 20)', city: 'Charlottesville, VA' },
        { name: 'Wake Forest', record: '4-8', ranking: 'NR', nextGame: 'vs Virginia (Jan 20)', city: 'Winston-Salem, NC' },
        { name: 'Georgia Tech', record: '7-6', ranking: 'NR', nextGame: 'vs Florida State (Jan 14)', city: 'Atlanta, GA' },
        { name: 'Florida State', record: '2-10', ranking: 'NR', nextGame: 'vs Georgia Tech (Jan 14)', city: 'Tallahassee, FL' },
        { name: 'Boston College', record: '7-6', ranking: 'NR', nextGame: 'vs California (Jan 21)', city: 'Chestnut Hill, MA' },
        { name: 'California', record: '6-7', ranking: 'NR', nextGame: 'vs Boston College (Jan 21)', city: 'Berkeley, CA' },
        { name: 'Stanford', record: '3-9', ranking: 'NR', nextGame: 'Season Complete', city: 'Stanford, CA' }
      ]
    }
  };

  const currentConference = conferenceData[selectedConference] || conferenceData[conference];

  const handleTeamClick = (teamName) => {
    if (!user) {
      // Show modal with both registration and sign-in options
      const modal = (
        <div className="access-modal-overlay">
          <div className="access-modal">
            <h3>Access Team Analysis</h3>
            <p>Sign in to view detailed team analysis, predictions, and avatar breakdowns for {teamName}.</p>
            <div className="modal-buttons">
              <button onClick={() => onShowSignIn && onShowSignIn()} className="btn-signin">
                Sign In
              </button>
              <button onClick={() => onShowRegistration && onShowRegistration()} className="btn-trial">
                Start Free Trial
              </button>
            </div>
            <button className="modal-close" onClick={() => setShowAccessModal(false)}>√ó</button>
          </div>
        </div>
      );
      setAccessModal(modal);
      setShowAccessModal(true);
    } else {
      navigate(`/team/${teamName.toLowerCase().replace(/\s+/g, '-').replace(/&/g, 'and')}`);
    }
  };

  const [showAccessModal, setShowAccessModal] = useState(false);
  const [accessModal, setAccessModal] = useState(null);

  const filteredTeams = currentConference?.teams.filter(team =>
    team.name.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  if (!currentConference) {
    return (
      <div className="conference-page">
        <div className="conference-header">
          <h1>Conference Not Found</h1>
          <Link to="/" className="back-link">‚Üê Back to Home</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="conference-page">
      {/* Conference Header */}
      <div className="conference-header">
        <div className="header-content">
          <div className="conference-info">
            <Link to="/" className="back-link">‚Üê All Conferences</Link>
            <h1>{currentConference.fullName}</h1>
            <p className="conference-description">
              View detailed analytics and predictions for all {currentConference.name} teams
            </p>
          </div>
          
          {/* Conference Switcher */}
          <div className="conference-switcher">
            <label>Switch Conference:</label>
            <select 
              value={selectedConference} 
              onChange={(e) => {
                setSelectedConference(e.target.value);
                navigate(`/conference/${e.target.value}`);
              }}
            >
              <option value="sec">SEC</option>
              <option value="big-ten">Big Ten</option>
              <option value="big-12">Big 12</option>
              <option value="acc">ACC</option>
            </select>
          </div>
        </div>
      </div>

      {/* Search Bar */}
      <div className="search-section">
        <div className="search-container">
          <input
            type="text"
            placeholder={`Search ${currentConference.name} teams...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <span className="search-icon">üîç</span>
        </div>
      </div>

      {/* Teams Grid */}
      <div className="teams-section">
        <div className="teams-grid">
          {filteredTeams.map((team, index) => (
            <div 
              key={team.name} 
              className="team-card"
              onClick={() => handleTeamClick(team.name)}
            >
              <div className="team-header">
                <h3 className="team-name">{team.name}</h3>
                <span className="team-ranking">
                  #{team.ranking === 'NR' ? 'NR' : team.ranking}
                </span>
              </div>
              
              <div className="team-details">
                <div className="team-record">
                  <span className="label">Record:</span>
                  <span className="value">{team.record}</span>
                </div>
                
                <div className="team-location">
                  <span className="label">Location:</span>
                  <span className="value">{team.city}</span>
                </div>
                
                <div className="next-game">
                  <span className="label">Next Game:</span>
                  <span className="value">{team.nextGame}</span>
                </div>
              </div>

              <div className="team-actions">
                <div className="prediction-preview">
                  <span className="prediction-label">Our Prediction:</span>
                  <span className="prediction-confidence">85% Win Probability</span>
                </div>
                <button className="view-analysis-btn">
                  {user ? 'View Analysis' : 'Sign In for Analysis'}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Access Modal */}
      {showAccessModal && accessModal}
    </div>
  );
};

export default ConferencePage;
