import React, { useState, useEffect } from 'react';
import { API, graphqlOperation } from 'aws-amplify';
import './TriviaGame.css';

const TriviaGame = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [gameActive, setGameActive] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);
  const [userStats, setUserStats] = useState({});
  const [timeLeft, setTimeLeft] = useState(15);

  // Mock trivia questions - replace with dynamic data
  const questions = [
    {
      question: "Which team has the most College Football National Championships?",
      options: ["Alabama", "Yale", "Princeton", "Harvard"],
      correct: 1, // Yale with 27
      explanation: "Yale holds the record with 27 claimed national championships, though most were in the early era of college football."
    },
    {
      question: "What is the capacity of the largest college football stadium?",
      options: ["107,601", "102,733", "104,944", "106,572"],
      correct: 2, // Michigan Stadium
      explanation: "Michigan Stadium (The Big House) has a capacity of 107,601, making it the largest college football stadium."
    },
    {
      question: "Which conference has won the most College Football Playoff National Championships?",
      options: ["SEC", "ACC", "Big Ten", "Pac-12"],
      correct: 0, // SEC
      explanation: "The SEC has dominated the College Football Playoff era with multiple national championships."
    },
    {
      question: "Who holds the record for most passing yards in a single college football game?",
      options: ["Connor Halliday", "Case Keenum", "Timmy Chang", "Graham Harrell"],
      correct: 0, // Connor Halliday - 734 yards
      explanation: "Connor Halliday of Washington State threw for 734 yards against Cal in 2014."
    },
    {
      question: "Which team is known as 'The Tide'?",
      options: ["Auburn", "Alabama", "LSU", "Tennessee"],
      correct: 1,
      explanation: "Alabama is known as the Crimson Tide, one of college football's most successful programs."
    }
  ];

  const [selectedAnswers, setSelectedAnswers] = useState(Array(questions.length).fill(null));
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    if (gameActive && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && gameActive) {
      handleNextQuestion();
    }
  }, [gameActive, timeLeft]);

  useEffect(() => {
    loadLeaderboard();
    loadUserStats();
  }, []);

  const loadLeaderboard = async () => {
    try {
      // Mock leaderboard data - replace with real API call
      const mockLeaderboard = [
        { rank: 1, username: "CFBExpert2024", points: 2847, streak: 12 },
        { rank: 2, username: "TideRising", points: 2691, streak: 8 },
        { rank: 3, username: "BuckeyeFan", points: 2573, streak: 15 },
        { rank: 4, username: "DuckHunter", points: 2445, streak: 6 },
        { rank: 5, username: "LonghornsRule", points: 2387, streak: 9 },
        { rank: 6, username: "GeauxTigers", points: 2156, streak: 4 },
        { rank: 7, username: "RollTide2024", points: 1998, streak: 7 },
        { rank: 8, username: "GoBlue", points: 1876, streak: 3 },
        { rank: 9, username: "WolverineWins", points: 1754, streak: 11 },
        { rank: 10, username: "SoonerNation", points: 1632, streak: 5 }
      ];
      setLeaderboard(mockLeaderboard);
    } catch (error) {
      console.error('Error loading leaderboard:', error);
    }
  };

  const loadUserStats = async () => {
    try {
      // Mock user stats - replace with real API call
      setUserStats({
        totalPoints: 1234,
        weeklyRank: 23,
        allTimeRank: 156,
        currentStreak: 5,
        correctAnswers: 89,
        totalAnswers: 127
      });
    } catch (error) {
      console.error('Error loading user stats:', error);
    }
  };

  const startGame = () => {
    setGameActive(true);
    setCurrentQuestion(0);
    setScore(0);
    setSelectedAnswers(Array(questions.length).fill(null));
    setShowResults(false);
    setTimeLeft(15);
  };

  const handleAnswerSelect = (answerIndex) => {
    if (!gameActive) return;
    
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestion] = answerIndex;
    setSelectedAnswers(newAnswers);
  };

  const handleNextQuestion = () => {
    // Calculate score for current question
    let newScore = score;
    const isCorrect = selectedAnswers[currentQuestion] === questions[currentQuestion].correct;
    
    if (isCorrect) {
      const timeBonus = Math.max(0, timeLeft * 10); // Bonus points for faster answers
      newScore += 100 + timeBonus;
    }
    
    setScore(newScore);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setTimeLeft(15);
    } else {
      endGame(newScore);
    }
  };

  const endGame = async (finalScore) => {
    setGameActive(false);
    setShowResults(true);
    
    // Submit score to backend
    try {
      await API.post('trivia', '/submit-score', {
        body: {
          score: finalScore,
          correctAnswers: selectedAnswers.filter((answer, index) => 
            answer === questions[index].correct
          ).length,
          totalQuestions: questions.length
        }
      });
      
      // Reload leaderboard and user stats
      await loadLeaderboard();
      await loadUserStats();
    } catch (error) {
      console.error('Error submitting score:', error);
    }
  };

  return (
    <div className="trivia-game">
      <div className="trivia-header">
        <h1>üéØ CFB Trivia Challenge</h1>
        <p className="trivia-subtitle">Test your college football knowledge!</p>
      </div>

      <div className="trivia-content">
        {/* User Stats Panel */}
        <div className="user-stats-panel">
          <h3>üìä Your Stats</h3>
          <div className="stats-grid">
            <div className="stat-item">
              <span className="stat-value">{userStats.totalPoints || 0}</span>
              <span className="stat-label">Total Points</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">#{userStats.weeklyRank || '--'}</span>
              <span className="stat-label">Weekly Rank</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{userStats.currentStreak || 0}</span>
              <span className="stat-label">Current Streak</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">
                {userStats.correctAnswers || 0}/{userStats.totalAnswers || 0}
              </span>
              <span className="stat-label">Accuracy</span>
            </div>
          </div>
        </div>

        {/* Game Area */}
        <div className="game-area">
          {!gameActive && !showResults && (
            <div className="game-start">
              <h2>üèà Weekly Trivia Challenge</h2>
              <div className="game-info">
                <p>‚ú® Answer 5 questions about college football</p>
                <p>‚è∞ 15 seconds per question</p>
                <p>üèÜ Compete for the weekly leaderboard</p>
                <p>üéÅ Top scorer each week wins a free month!</p>
              </div>
              <button className="start-game-btn" onClick={startGame}>
                Start Trivia Challenge
              </button>
            </div>
          )}

          {gameActive && (
            <div className="game-active">
              <div className="question-header">
                <div className="progress-bar">
                  <div 
                    className="progress-fill"
                    style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                  ></div>
                </div>
                <div className="question-info">
                  <span className="question-number">
                    Question {currentQuestion + 1} of {questions.length}
                  </span>
                  <span className="timer">‚è∞ {timeLeft}s</span>
                  <span className="score">Score: {score}</span>
                </div>
              </div>

              <div className="question-content">
                <h3 className="question-text">
                  {questions[currentQuestion].question}
                </h3>
                
                <div className="options-grid">
                  {questions[currentQuestion].options.map((option, index) => (
                    <button
                      key={index}
                      className={`option-btn ${
                        selectedAnswers[currentQuestion] === index ? 'selected' : ''
                      }`}
                      onClick={() => handleAnswerSelect(index)}
                    >
                      {String.fromCharCode(65 + index)}. {option}
                    </button>
                  ))}
                </div>

                <button 
                  className="next-btn"
                  onClick={handleNextQuestion}
                  disabled={selectedAnswers[currentQuestion] === null}
                >
                  {currentQuestion < questions.length - 1 ? 'Next Question' : 'Finish Game'}
                </button>
              </div>
            </div>
          )}

          {showResults && (
            <div className="game-results">
              <h2>üéä Game Complete!</h2>
              <div className="final-score">
                <span className="score-value">{score}</span>
                <span className="score-label">Final Score</span>
              </div>

              <div className="results-summary">
                <p>
                  You answered {selectedAnswers.filter((answer, index) => 
                    answer === questions[index].correct
                  ).length} out of {questions.length} questions correctly!
                </p>
              </div>

              <div className="question-review">
                <h3>üìù Question Review</h3>
                {questions.map((q, index) => (
                  <div key={index} className="review-item">
                    <p className="review-question">{q.question}</p>
                    <div className="review-answer">
                      <span className={`user-answer ${
                        selectedAnswers[index] === q.correct ? 'correct' : 'incorrect'
                      }`}>
                        Your answer: {q.options[selectedAnswers[index]] || 'No answer'}
                      </span>
                      {selectedAnswers[index] !== q.correct && (
                        <span className="correct-answer">
                          Correct: {q.options[q.correct]}
                        </span>
                      )}
                    </div>
                    <p className="explanation">{q.explanation}</p>
                  </div>
                ))}
              </div>

              <button className="play-again-btn" onClick={startGame}>
                üîÑ Play Again
              </button>
            </div>
          )}
        </div>

        {/* Leaderboard */}
        <div className="leaderboard-panel">
          <h3>üèÜ Weekly Leaderboard</h3>
          <div className="leaderboard">
            {leaderboard.map((entry) => (
              <div key={entry.rank} className="leaderboard-entry">
                <span className="rank">#{entry.rank}</span>
                <span className="username">{entry.username}</span>
                <span className="points">{entry.points.toLocaleString()}</span>
                <span className="streak">üî•{entry.streak}</span>
              </div>
            ))}
          </div>
          
          <div className="leaderboard-prize">
            <p>üéÅ <strong>Weekly Prize:</strong> Top scorer wins a free month of Premium!</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TriviaGame;
