import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ user, onLogin, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isConferenceDropdownOpen, setIsConferenceDropdownOpen] = useState(false);
  const location = useLocation();

  const conferences = [
    { id: 'sec', name: 'SEC', teams: 16 },
    { id: 'big-ten', name: 'Big Ten', teams: 18 },
    { id: 'big-12', name: 'Big 12', teams: 16 },
    { id: 'acc', name: 'ACC', teams: 17 },
    { id: 'pac-12', name: 'Pac-12', teams: 12 },
    { id: 'group-of-five', name: 'Group of 5', teams: 65 }
  ];

  const isActivePage = (path) => {
    return location.pathname === path;
  };

  const handleConferenceClick = () => {
    setIsConferenceDropdownOpen(!isConferenceDropdownOpen);
  };

  return (
    <nav className="modern-navbar">
      <div className="navbar-container">
        {/* Logo/Brand Section */}
        <div className="navbar-brand">
          <Link to="/" className="brand-link">
            <div className="brand-logo">🏈</div>
            <div className="brand-text">
              <span className="brand-name">CFB Analytics</span>
              <span className="brand-tagline">92.3% Accuracy</span>
            </div>
          </Link>
        </div>

        {/* Main Navigation Links */}
        <div className={`navbar-menu ${isMenuOpen ? 'is-active' : ''}`}>
          <div className="navbar-nav">
            
            {/* Home */}
            <Link 
              to="/" 
              className={`nav-item ${isActivePage('/') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              <span className="nav-icon">🏠</span>
              <span className="nav-text">Home</span>
            </Link>

            {/* Conferences Dropdown */}
            <div className="nav-item dropdown">
              <button 
                className="nav-dropdown-trigger"
                onClick={handleConferenceClick}
                onBlur={() => setTimeout(() => setIsConferenceDropdownOpen(false), 150)}
              >
                <span className="nav-icon">🏟️</span>
                <span className="nav-text">Conferences</span>
                <span className="dropdown-arrow">▼</span>
              </button>
              
              {isConferenceDropdownOpen && (
                <div className="dropdown-menu">
                  {conferences.map(conference => (
                    <Link
                      key={conference.id}
                      to={`/conference/${conference.id}`}
                      className="dropdown-item"
                      onClick={() => {
                        setIsConferenceDropdownOpen(false);
                        setIsMenuOpen(false);
                      }}
                    >
                      <span className="conference-name">{conference.name}</span>
                      <span className="team-count">{conference.teams} teams</span>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            {/* Community Chat */}
            <Link 
              to="/chat" 
              className={`nav-item ${isActivePage('/chat') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              <span className="nav-icon">💬</span>
              <span className="nav-text">Community</span>
            </Link>

            {/* Trivia */}
            <Link 
              to="/trivia" 
              className={`nav-item ${isActivePage('/trivia') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              <span className="nav-icon">🧠</span>
              <span className="nav-text">Trivia</span>
            </Link>

            {/* Premium Predictions */}
            <Link 
              to="/predictions" 
              className={`nav-item premium ${isActivePage('/predictions') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              <span className="nav-icon">🎯</span>
              <span className="nav-text">Predictions</span>
              <span className="premium-badge">PRO</span>
            </Link>
          </div>
        </div>

        {/* User Section */}
        <div className="navbar-user">
          {user ? (
            <div className="user-menu">
              <div className="user-info">
                <span className="user-name">👋 {user.username}</span>
                <span className="user-status">
                  {user.subscription === 'premium' ? '🏆 Premium' : '🆓 Free Trial'}
                </span>
              </div>
              <button className="logout-btn" onClick={onLogout}>
                Sign Out
              </button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button className="trial-btn" onClick={onLogin}>
                🚀 Start Free Trial
              </button>
              <button className="login-btn" onClick={onLogin}>
                Sign In
              </button>
            </div>
          )}
        </div>

        {/* Mobile Menu Toggle */}
        <button 
          className="mobile-menu-toggle"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
        </button>
      </div>

      {/* Accuracy Banner */}
      <div className="accuracy-banner">
        <div className="banner-container">
          <span className="accuracy-stat">📊 92.3% Accuracy This Season</span>
          <span className="banner-separator">•</span>
          <span className="data-points">320K+ Data Points</span>
          <span className="banner-separator">•</span>
          <span className="games-analyzed">10,484 Games Analyzed</span>
          <span className="banner-separator">•</span>
          <span className="entertainment-notice">For Entertainment Only</span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
