import React, { useState, useEffect } from 'react';
import './ConferencePage.css';

const EnhancedConferencePage = () => {
  const [activeView, setActiveView] = useState('power4');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedConference, setSelectedConference] = useState(null);

  // Power 4 Conferences (Main Display)
  const power4Conferences = [
    {
      name: 'SEC',
      teams: ['Alabama', 'Georgia', 'LSU', 'Tennessee', 'Auburn', 'Florida', 'Kentucky', 'South Carolina', 'Mississippi State', 'Ole Miss', 'Arkansas', 'Missouri', 'Vanderbilt', 'Texas A&M', 'Texas', 'Oklahoma'],
      color: '#1f4e79',
      icon: '⭐'
    },
    {
      name: 'Big Ten',
      teams: ['Ohio State', 'Michigan', 'Penn State', 'Wisconsin', 'Iowa', 'Minnesota', 'Illinois', 'Northwestern', 'Purdue', 'Indiana', 'Michigan State', 'Nebraska', 'Maryland', 'Rutgers', 'UCLA', 'USC', 'Oregon', 'Washington'],
      color: '#8B0000',
      icon: '🔟'
    },
    {
      name: 'Big 12',
      teams: ['Oklahoma State', 'Kansas State', 'TCU', 'Baylor', 'Texas Tech', 'Kansas', 'Iowa State', 'West Virginia', 'Cincinnati', 'Houston', 'UCF', 'BYU', 'Arizona', 'Arizona State', 'Colorado', 'Utah'],
      color: '#FF6B35',
      icon: '1️⃣2️⃣'
    },
    {
      name: 'ACC',
      teams: ['Clemson', 'Miami', 'Florida State', 'NC State', 'Wake Forest', 'Duke', 'North Carolina', 'Virginia', 'Virginia Tech', 'Georgia Tech', 'Pittsburgh', 'Syracuse', 'Boston College', 'Louisville', 'Notre Dame', 'Stanford', 'Cal', 'SMU'],
      color: '#4169E1',
      icon: '🏛️'
    }
  ];

  // Group of Five & Others (Secondary Display)
  const otherConferences = [
    {
      category: 'Group of Five',
      conferences: [
        {
          name: 'American Athletic',
          teams: ['Memphis', 'Temple', 'Tulane', 'Navy', 'ECU', 'South Florida', 'Tulsa', 'Wichita State', 'Charlotte', 'UAB', 'UTSA', 'Rice', 'Florida Atlantic', 'North Texas'],
          bettingAvailable: true
        },
        {
          name: 'Mountain West',
          teams: ['Boise State', 'Fresno State', 'San Diego State', 'Air Force', 'Colorado State', 'Wyoming', 'New Mexico', 'UNLV', 'Nevada', 'San Jose State', 'Utah State', 'Hawaii'],
          bettingAvailable: true
        },
        {
          name: 'Conference USA',
          teams: ['Marshall', 'Old Dominion', 'Southern Miss', 'Florida International', 'Middle Tennessee', 'Western Kentucky', 'Louisiana Tech', 'UTEP', 'New Mexico State', 'Liberty', 'Sam Houston', 'Jacksonville State'],
          bettingAvailable: true
        },
        {
          name: 'MAC',
          teams: ['Northern Illinois', 'Toledo', 'Ohio', 'Miami (OH)', 'Buffalo', 'Bowling Green', 'Kent State', 'Akron', 'Ball State', 'Central Michigan', 'Eastern Michigan', 'Western Michigan'],
          bettingAvailable: true
        },
        {
          name: 'Sun Belt',
          teams: ['App State', 'Coastal Carolina', 'Georgia Southern', 'Georgia State', 'James Madison', 'Marshall', 'Old Dominion', 'Southern Miss', 'Troy', 'Arkansas State', 'Louisiana', 'South Alabama', 'Texas State', 'ULM'],
          bettingAvailable: true
        }
      ]
    },
    {
      category: 'FCS Conferences',
      note: 'Featured in FBS vs FCS matchups with betting lines',
      conferences: [
        {
          name: 'Big Sky',
          teams: ['Montana', 'Montana State', 'Northern Arizona', 'Weber State', 'Idaho State', 'Portland State', 'Eastern Washington', 'Idaho', 'Sacramento State'],
          bettingAvailable: 'FBS Matchups Only'
        },
        {
          name: 'CAA',
          teams: ['Delaware', 'James Madison', 'Richmond', 'William & Mary', 'Villanova', 'New Hampshire', 'Maine', 'Rhode Island'],
          bettingAvailable: 'FBS Matchups Only'
        },
        {
          name: 'Missouri Valley',
          teams: ['North Dakota State', 'South Dakota State', 'Northern Iowa', 'Illinois State', 'Indiana State', 'Missouri State', 'Southern Illinois', 'Western Illinois'],
          bettingAvailable: 'FBS Matchups Only'
        },
        {
          name: 'Southland',
          teams: ['Sam Houston', 'Stephen F. Austin', 'Nicholls', 'Northwestern State', 'McNeese', 'Lamar', 'Houston Christian', 'Incarnate Word'],
          bettingAvailable: 'FBS Matchups Only'
        }
      ]
    }
  ];

  // Upcoming FBS vs FCS games with betting interest
  const featuredMatchups = [
    { game: 'Northern Arizona vs Arizona State', week: 1, bettingLine: 'ASU -42.5' },
    { game: 'Portland State vs BYU', week: 1, bettingLine: 'BYU -34.5' },
    { game: 'Montana State vs Oregon', week: 2, bettingLine: 'Oregon -28.5' },
    { game: 'Idaho vs Washington State', week: 1, bettingLine: 'WSU -31.5' },
    { game: 'Weber State vs James Madison', week: 3, bettingLine: 'JMU -14.5' },
    { game: 'Holy Cross vs Northern Illinois', week: 1, bettingLine: 'NIU -21.5' }
  ];

  const filteredTeams = (teams) => {
    if (!searchTerm) return teams;
    return teams.filter(team => 
      team.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  return (
    <div className="enhanced-conference-page">
      {/* Header with Toggle */}
      <div className="conference-header">
        <h1>🏈 Complete College Football Coverage</h1>
        <p className="subtitle">
          92.3% Accurate Predictions • All 136 Teams • 320,000+ Data Points
        </p>
        
        {/* View Toggle */}
        <div className="view-toggle">
          <button 
            className={activeView === 'power4' ? 'active' : ''}
            onClick={() => setActiveView('power4')}
          >
            Power 4 Conferences
          </button>
          <button 
            className={activeView === 'other' ? 'active' : ''}
            onClick={() => setActiveView('other')}
          >
            Other Conferences & Matchups
          </button>
          <button 
            className={activeView === 'search' ? 'active' : ''}
            onClick={() => setActiveView('search')}
          >
            Find Any Team
          </button>
        </div>
      </div>

      {/* Search Bar (always visible) */}
      <div className="search-section">
        <input
          type="text"
          placeholder="Search any team... (e.g., 'Northern Arizona', 'Weber State', 'Toledo')"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="team-search"
        />
      </div>

      {/* Power 4 View */}
      {activeView === 'power4' && (
        <div className="power4-section">
          <div className="conferences-grid">
            {power4Conferences.map((conf) => (
              <div key={conf.name} className="conference-card major" style={{borderColor: conf.color}}>
                <div className="conference-header" style={{backgroundColor: conf.color}}>
                  <span className="conference-icon">{conf.icon}</span>
                  <h3>{conf.name}</h3>
                  <span className="team-count">{conf.teams.length} teams</span>
                </div>
                <div className="teams-grid">
                  {filteredTeams(conf.teams).map((team) => (
                    <div key={team} className="team-item">
                      <span className="team-name">{team}</span>
                      <span className="prediction-indicator">📊</span>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          
          {/* Teaser for Other Conferences */}
          <div className="other-conferences-teaser">
            <h3>🔍 Also Covering 70+ Additional Teams</h3>
            <p>Group of Five, FCS powerhouses, and all teams with betting lines available</p>
            <button onClick={() => setActiveView('other')} className="explore-button">
              Explore All Teams & Conferences
            </button>
          </div>
        </div>
      )}

      {/* Other Conferences View */}
      {activeView === 'other' && (
        <div className="other-section">
          {/* Featured FBS vs FCS Matchups */}
          <div className="featured-matchups">
            <h2>🎯 Featured Matchups with Betting Lines</h2>
            <p className="section-note">
              These games have active betting markets and generate significant interest
            </p>
            <div className="matchups-grid">
              {featuredMatchups.map((matchup, index) => (
                <div key={index} className="matchup-card">
                  <div className="matchup-teams">{matchup.game}</div>
                  <div className="matchup-details">
                    <span className="week">Week {matchup.week}</span>
                    <span className="betting-line">{matchup.bettingLine}</span>
                  </div>
                  <div className="betting-indicator">📈 Betting Available</div>
                </div>
              ))}
            </div>
          </div>

          {/* All Other Conferences */}
          {otherConferences.map((category) => (
            <div key={category.category} className="conference-category">
              <h2>{category.category}</h2>
              {category.note && <p className="category-note">{category.note}</p>}
              
              <div className="conferences-grid">
                {category.conferences.map((conf) => (
                  <div key={conf.name} className="conference-card secondary">
                    <div className="conference-header">
                      <h4>{conf.name}</h4>
                      <div className="betting-status">
                        {conf.bettingAvailable === true && <span className="betting-yes">💰 Betting Lines</span>}
                        {conf.bettingAvailable === 'FBS Matchups Only' && <span className="betting-partial">📊 FBS Games</span>}
                      </div>
                    </div>
                    <div className="teams-list">
                      {filteredTeams(conf.teams).map((team) => (
                        <div key={team} className="team-item-small">
                          {team}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Search View */}
      {activeView === 'search' && (
        <div className="search-results">
          <h2>🔍 Find Any Team</h2>
          <p>Search our complete database of 136 college football teams</p>
          
          {searchTerm && (
            <div className="search-results-grid">
              {/* Search through all teams and display results */}
              <div className="search-placeholder">
                <p>Searching for "{searchTerm}"...</p>
                <p>Results would show matching teams from all conferences with:</p>
                <ul>
                  <li>Team analytics and predictions</li>
                  <li>Upcoming game schedule</li>
                  <li>Betting line availability</li>
                  <li>Historical performance data</li>
                </ul>
              </div>
            </div>
          )}
        </div>
      )}

      {/* SEO Footer */}
      <div className="seo-footer">
        <h3>Complete College Football Coverage</h3>
        <p>
          We analyze every team, every game, every matchup. From Alabama vs Georgia 
          to Northern Arizona vs Arizona State. Our AI doesn't discriminate - 
          we provide 92.3% accurate predictions for all 136 FBS and featured FCS teams.
        </p>
      </div>
    </div>
  );
};

export default EnhancedConferencePage;
