import React, { useState, useEffect } from 'react';
import { API, graphqlOperation } from 'aws-amplify';
import './BroadcastBooth.css';

const LIST_PREDICTIONS = `
  query ListPredictions($filter: ModelPredictionFilterInput) {
    listPredictions(filter: $filter) {
      items {
        id
        gameID
        predictedHomeScore
        predictedAwayScore
        confidence
        expectedBoxScore
        game {
          id
          date
          location
          homeTeam {
            name
            logoUrl
            conference
          }
          awayTeam {
            name
            logoUrl  
            conference
          }
        }
      }
    }
  }
`;

const BroadcastBooth = () => {
  const [predictions, setPredictions] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [loading, setLoading] = useState(true);
  const [weekFilter, setWeekFilter] = useState('upcoming');

  useEffect(() => {
    loadPredictions();
  }, [weekFilter]);

  const loadPredictions = async () => {
    try {
      setLoading(true);
      const result = await API.graphql(graphqlOperation(LIST_PREDICTIONS));
      setPredictions(result.data.listPredictions.items);
    } catch (error) {
      console.error('Error loading predictions:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatConfidence = (confidence) => {
    return `${Math.round(confidence * 100)}%`;
  };

  const getWinnerInfo = (prediction) => {
    const homeScore = prediction.predictedHomeScore;
    const awayScore = prediction.predictedAwayScore;
    
    if (homeScore > awayScore) {
      return {
        winner: prediction.game.homeTeam,
        loser: prediction.game.awayTeam,
        spread: homeScore - awayScore
      };
    } else {
      return {
        winner: prediction.game.awayTeam,
        loser: prediction.game.homeTeam,
        spread: awayScore - homeScore
      };
    }
  };

  if (loading) {
    return (
      <div className="broadcast-booth-loading">
        <div className="spinner"></div>
        <p>Loading predictions from the 54-Factor Engine...</p>
      </div>
    );
  }

  return (
    <div className="broadcast-booth">
      {/* ESPN-Style Header */}
      <div className="broadcast-header">
        <div className="logo-section">
          <h1>üèà CFB Analytics</h1>
          <span className="tagline">Powered by 54-Factor AI Engine</span>
        </div>
        <div className="accuracy-badge">
          <span className="accuracy-number">92.3%</span>
          <span className="accuracy-label">Season Accuracy</span>
        </div>
      </div>

      {/* Week Selector */}
      <div className="week-selector">
        <button 
          className={weekFilter === 'upcoming' ? 'active' : ''}
          onClick={() => setWeekFilter('upcoming')}
        >
          This Week
        </button>
        <button 
          className={weekFilter === 'all' ? 'active' : ''}
          onClick={() => setWeekFilter('all')}
        >
          All Predictions
        </button>
      </div>

      {/* Predictions Grid */}
      <div className="predictions-grid">
        {predictions.map((prediction) => {
          const winnerInfo = getWinnerInfo(prediction);
          
          return (
            <div 
              key={prediction.id}
              className="prediction-card"
              onClick={() => setSelectedGame(prediction)}
            >
              <div className="game-header">
                <span className="game-date">
                  {new Date(prediction.game.date).toLocaleDateString()}
                </span>
                <span className="confidence-badge">
                  {formatConfidence(prediction.confidence)}
                </span>
              </div>

              <div className="matchup">
                <div className="team away-team">
                  <img 
                    src={prediction.game.awayTeam.logoUrl} 
                    alt={prediction.game.awayTeam.name}
                    className="team-logo"
                  />
                  <div className="team-info">
                    <span className="team-name">{prediction.game.awayTeam.name}</span>
                    <span className="team-conference">{prediction.game.awayTeam.conference}</span>
                  </div>
                  <div className="predicted-score">
                    {prediction.predictedAwayScore}
                  </div>
                </div>

                <div className="vs-section">
                  <span className="at-symbol">@</span>
                </div>

                <div className="team home-team">
                  <div className="predicted-score">
                    {prediction.predictedHomeScore}
                  </div>
                  <div className="team-info">
                    <span className="team-name">{prediction.game.homeTeam.name}</span>
                    <span className="team-conference">{prediction.game.homeTeam.conference}</span>
                  </div>
                  <img 
                    src={prediction.game.homeTeam.logoUrl} 
                    alt={prediction.game.homeTeam.name}
                    className="team-logo"
                  />
                </div>
              </div>

              <div className="prediction-details">
                <div className="winner-info">
                  <span className="winner-label">Predicted Winner:</span>
                  <span className="winner-name">{winnerInfo.winner.name}</span>
                  <span className="spread">by {winnerInfo.spread}</span>
                </div>
                <div className="location">
                  üìç {prediction.game.location}
                </div>
              </div>

              <div className="ai-insights">
                <span className="insights-label">üß† AI Key Factors:</span>
                <div className="factors-preview">
                  Home Field Advantage ‚Ä¢ Emerging Players ‚Ä¢ Weather Impact
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Detailed Game Modal */}
      {selectedGame && (
        <GameDetailModal 
          game={selectedGame}
          onClose={() => setSelectedGame(null)}
        />
      )}

      {/* Today's Top Picks */}
      <div className="top-picks-section">
        <h2>üéØ Today's Top Picks</h2>
        <div className="top-picks-grid">
          {predictions
            .filter(p => p.confidence > 0.85)
            .slice(0, 3)
            .map(prediction => (
              <TopPickCard key={prediction.id} prediction={prediction} />
            ))}
        </div>
      </div>

      {/* Emerging Players Section */}
      <div className="emerging-players-section">
        <h2>‚≠ê Emerging Players to Watch</h2>
        <EmergingPlayersWidget />
      </div>
    </div>
  );
};

// Game Detail Modal Component
const GameDetailModal = ({ game, onClose }) => {
  const factors = JSON.parse(game.expectedBoxScore || '{}').factors || {};
  
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="game-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>54-Factor Analysis</h2>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>

        <div className="modal-content">
          <div className="factor-categories">
            <div className="factor-category">
              <h3>üè† Home Field Advantage</h3>
              <div className="factor-bar">
                <div 
                  className="factor-fill" 
                  style={{width: `${(factors.trueHomeFieldAdvantage || 0.5) * 100}%`}}
                ></div>
              </div>
              <span>{Math.round((factors.trueHomeFieldAdvantage || 0.5) * 100)}%</span>
            </div>

            <div className="factor-category">
              <h3>üìä Recent Performance</h3>
              <div className="factor-bar">
                <div 
                  className="factor-fill" 
                  style={{width: `${(factors.recentPerformanceTrend || 0.5) * 100}%`}}
                ></div>
              </div>
              <span>{Math.round((factors.recentPerformanceTrend || 0.5) * 100)}%</span>
            </div>

            <div className="factor-category">
              <h3>‚≠ê Emerging Player Impact</h3>
              <div className="factor-bar">
                <div 
                  className="factor-fill emerging" 
                  style={{width: `${Math.abs(factors.emergingPlayerImpact || 0) * 100}%`}}
                ></div>
              </div>
              <span>+{Math.round(Math.abs(factors.emergingPlayerImpact || 0) * 10)} pts</span>
            </div>

            <div className="factor-category">
              <h3>üå§Ô∏è Weather Impact</h3>
              <div className="factor-bar">
                <div 
                  className="factor-fill" 
                  style={{width: `${(factors.weatherImpact || 0.5) * 100}%`}}
                ></div>
              </div>
              <span>{factors.weatherCondition || 'Clear'}</span>
            </div>
          </div>

          <div className="prediction-summary">
            <h3>Final Prediction</h3>
            <div className="final-score">
              {game.game.homeTeam.name} {game.predictedHomeScore} - {game.predictedAwayScore} {game.game.awayTeam.name}
            </div>
            <div className="confidence-display">
              Confidence: {Math.round(game.confidence * 100)}%
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Top Pick Card Component
const TopPickCard = ({ prediction }) => (
  <div className="top-pick-card">
    <div className="pick-confidence">
      üî• {Math.round(prediction.confidence * 100)}% CONFIDENCE
    </div>
    <div className="pick-matchup">
      {prediction.game.awayTeam.name} @ {prediction.game.homeTeam.name}
    </div>
    <div className="pick-prediction">
      Take: {prediction.predictedHomeScore > prediction.predictedAwayScore ? 
        prediction.game.homeTeam.name : prediction.game.awayTeam.name}
    </div>
  </div>
);

// Emerging Players Widget
const EmergingPlayersWidget = () => {
  const [emergingPlayers, setEmergingPlayers] = useState([]);

  useEffect(() => {
    // Load emerging players data
    loadEmergingPlayers();
  }, []);

  const loadEmergingPlayers = async () => {
    try {
      const result = await API.graphql(graphqlOperation(`
        query ListEmergingPlayers {
          listEmergingPlayers(limit: 5) {
            items {
              id
              name
              team
              position
              projectedImpact
              confidence
              mentions
            }
          }
        }
      `));
      setEmergingPlayers(result.data.listEmergingPlayers.items);
    } catch (error) {
      console.error('Error loading emerging players:', error);
    }
  };

  return (
    <div className="emerging-players-widget">
      {emergingPlayers.map(player => (
        <div key={player.id} className="emerging-player-card">
          <div className="player-info">
            <span className="player-name">{player.name}</span>
            <span className="player-team">{player.team} - {player.position}</span>
          </div>
          <div className="player-metrics">
            <div className="impact-meter">
              <div 
                className="impact-fill"
                style={{width: `${player.projectedImpact * 100}%`}}
              ></div>
            </div>
            <span className="mentions-count">{player.mentions} mentions</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BroadcastBooth;
