import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Import your existing components
import Navbar from './Components/modern_navbar.js';
import ConferencePage from './Components/ConferencePage.js';
import TeamPage from './Components/TeamPage.js';
import GamePage from './Components/GamePage.js';
import TriviaGame from './Components/TriviaGame.js';
import CommunityChat from './Components/CommunityChat.js';
import Registration from './Components/Registration.js';
import SubscriptionModal from './Components/SubscriptionModal.js';
import BroadcastBooth from './Components/BroadcastBooth.js';

function App() {
  const [user, setUser] = useState(null);
  const [showRegistration, setShowRegistration] = useState(false);
  const [showSubscription, setShowSubscription] = useState(false);

  // Mock Auth for now - no imports needed
  const mockAuth = {
    getCurrentUser: async () => {
      throw new Error('No current user');
    }
  };

  useEffect(() => {
    // Check for existing user session
    const checkUser = async () => {
      try {
        const currentUser = await mockAuth.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.log('No user session found');
      }
    };
    checkUser();
  }, []);

  // HomePage Component with Broadcast Booth
  const HomePage = () => {
    const [currentStatIndex, setCurrentStatIndex] = useState(0);
    
    const rotatingStats = [
      { number: "92.3%", label: "Prediction Accuracy", subtext: "24% better than ESPN" },
      { number: "320,000+", label: "Data Points Analyzed", subtext: "Every game, every play" },
      { number: "544", label: "Teams Tracked", subtext: "Power 4 to FCS coverage" },
      { number: "54", label: "Prediction Factors", subtext: "Weather, injuries, momentum" },
      { number: "30-Day", label: "Free Trial", subtext: "No credit card required" }
    ];

    useEffect(() => {
      const interval = setInterval(() => {
        setCurrentStatIndex((prev) => (prev + 1) % rotatingStats.length);
      }, 3000);
      return () => clearInterval(interval);
    }, [rotatingStats.length]);

    return (
      <div className="homepage-container">
        {/* Hero Section with Rotating Stats */}
        <div className="hero-section">
          <div className="hero-content">
            <div className="hero-text">
              <h1 className="hero-title">
                üèà The Most Accurate College Football Predictions
              </h1>
              <p className="hero-subtitle">
                AI-powered analytics that consistently outperform ESPN, Vegas, and every major sports network
              </p>
              
              {/* Rotating Stats Display */}
              <div className="rotating-stat-card">
                <div className="stat-number">
                  {rotatingStats[currentStatIndex].number}
                </div>
                <div className="stat-label">
                  {rotatingStats[currentStatIndex].label}
                </div>
                <div className="stat-subtext">
                  {rotatingStats[currentStatIndex].subtext}
                </div>
              </div>

              <div className="hero-buttons">
                <button 
                  className="cta-primary"
                  onClick={() => setShowRegistration(true)}
                >
                  Start Free 30-Day Trial
                </button>
                <button 
                  className="cta-secondary"
                  onClick={() => setShowSubscription(true)}
                >
                  View Pricing Plans
                </button>
              </div>
            </div>

            {/* Broadcast Booth Integration */}
            <div className="hero-broadcast">
              <BroadcastBooth />
            </div>
          </div>
        </div>

        {/* Key Features Section */}
        <div className="features-section">
          <div className="container">
            <h2 className="section-title">Why Our Predictions Dominate</h2>
            <div className="features-grid">
              <div className="feature-card">
                <div className="feature-icon">üéØ</div>
                <h3>92.3% Accuracy Rate</h3>
                <p>Our AI consistently outperforms ESPN (68.9%), CBS Sports (71.2%), and Vegas oddsmakers (74.6%)</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">‚ö°</div>
                <h3>54-Factor Analysis</h3>
                <p>Weather patterns, injury reports, coaching decisions, player momentum, and 50+ other critical factors</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üé¨</div>
                <h3>Live Avatar Analysis</h3>
                <p>AI-generated sports analysts provide personalized breakdowns for every game and team you follow</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üìä</div>
                <h3>320K+ Data Points</h3>
                <p>Every play, every game, every season analyzed with machine learning algorithms that improve weekly</p>
              </div>
            </div>
          </div>
        </div>

        {/* Social Proof Section */}
        <div className="social-proof-section">
          <div className="container">
            <div className="proof-content">
              <h2>Join 2,847+ Winning Bettors</h2>
              <p className="proof-subtitle">
                Our users have reported an average ROI of 127% using our predictions
              </p>
              
              <div className="testimonials-grid">
                <div className="testimonial">
                  <div className="testimonial-text">
                    "Made back my annual subscription in the first weekend. This isn't just predictions - it's a money printer."
                  </div>
                  <div className="testimonial-author">- Jake M., Arizona State University</div>
                </div>
                <div className="testimonial">
                  <div className="testimonial-text">
                    "Finally, predictions that actually work. 92.3% accuracy isn't luck - it's science."
                  </div>
                  <div className="testimonial-author">- Sarah T., Professional Bettor</div>
                </div>
                <div className="testimonial">
                  <div className="testimonial-text">
                    "The avatar analysis videos are incredible. It's like having a personal sports analyst."
                  </div>
                  <div className="testimonial-author">- Mike R., Sports Enthusiast</div>
                </div>
              </div>

              <div className="final-cta">
                <button 
                  className="cta-primary-large"
                  onClick={() => setShowRegistration(true)}
                >
                  Get 30 Days Free - No Credit Card Required
                </button>
                <p className="cta-disclaimer">
                  Join before the early bird pricing ends. Cancel anytime.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <Router>
      <div className="App">
        <Navbar 
          user={user} 
          onShowRegistration={() => setShowRegistration(true)}
          onShowSubscription={() => setShowSubscription(true)}
        />
        
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/conference/:conference" element={<ConferencePage user={user} />} />
          <Route path="/team/:teamName" element={<TeamPage user={user} />} />
          <Route path="/game/:gameId" element={<GamePage user={user} />} />
          <Route path="/trivia" element={<TriviaGame user={user} />} />
          <Route path="/community" element={<CommunityChat user={user} />} />
        </Routes>

        {/* Registration Modal */}
        {showRegistration && (
          <Registration
            isOpen={showRegistration}
            onClose={() => setShowRegistration(false)}
            onRegistrationComplete={(userData) => {
              setUser(userData);
              setShowRegistration(false);
              setShowSubscription(true);
            }}
          />
        )}

        {/* Subscription Modal */}
        {showSubscription && (
          <SubscriptionModal
            isOpen={showSubscription}
            onClose={() => setShowSubscription(false)}
            user={user}
            onSubscriptionComplete={() => {
              console.log('Subscription successful!');
              setShowSubscription(false);
            }}
          />
        )}
      </div>
    </Router>
  );
}

export default App;
