import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Import your working components (note the correct file names)
import Navbar from './Components/modern_navbar.js';
import ConferencePage from './Components/ConferencePage.js';
import TeamPage from './Components/TeamPage.js';
import GamePage from './Components/GamePage.js';
import CommunityChat from './Components/CommunityChat.js';
import TriviaGame from './Components/TriviaGame.js';
import RegistrationSystem from './Components/Registration.js';
import SubscriptionModal from './Components/SubscriptionModal.js';

// Simple Homepage Component (since BroadcastBooth may have issues)
const HomePage = ({ user, onSubscriptionClick }) => {
  const [currentStat, setCurrentStat] = useState(0);
  
  const stats = [
    { label: "Prediction Accuracy", value: "92.3%", icon: "🎯" },
    { label: "Data Points Analyzed", value: "320,000+", icon: "📊" },
    { label: "Teams Covered", value: "544", icon: "🏈" },
    { label: "Games Predicted", value: "10,484", icon: "🔮" }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentStat((prev) => (prev + 1) % stats.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [stats.length]);

  return (
    <div style={{
      minHeight: '90vh',
      background: 'linear-gradient(135deg, #1e3c72 0%, #2a5298 100%)',
      color: 'white',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      textAlign: 'center'
    }}>
      <div style={{ maxWidth: '800px' }}>
        <h1 style={{ 
          fontSize: '3.5rem', 
          marginBottom: '20px',
          background: 'linear-gradient(45deg, #FFD700, #FFA500)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text'
        }}>
          🏈 CFB Analytics
        </h1>
        
        <p style={{ fontSize: '1.5rem', marginBottom: '40px', opacity: 0.9 }}>
          AI-Powered College Football Predictions with 92.3% Accuracy
        </p>

        {/* Rotating Stats */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          borderRadius: '20px',
          padding: '40px',
          marginBottom: '40px',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        }}>
          <div style={{ fontSize: '3rem', marginBottom: '10px' }}>
            {stats[currentStat].icon}
          </div>
          <div style={{ fontSize: '3rem', fontWeight: 'bold', marginBottom: '10px' }}>
            {stats[currentStat].value}
          </div>
          <div style={{ fontSize: '1.2rem', opacity: 0.8 }}>
            {stats[currentStat].label}
          </div>
        </div>

        {/* Call to Action */}
        <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', flexWrap: 'wrap' }}>
          <button
            onClick={onSubscriptionClick}
            style={{
              background: 'linear-gradient(45deg, #4CAF50, #45a049)',
              color: 'white',
              border: 'none',
              padding: '15px 30px',
              borderRadius: '25px',
              fontSize: '1.1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'transform 0.3s ease',
              boxShadow: '0 4px 15px rgba(76, 175, 80, 0.4)'
            }}
            onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
            onMouseOut={(e) => e.target.style.transform = 'translateY(0px)'}
          >
            🚀 Start Free Trial
          </button>
          
          <a 
            href="/conferences"
            style={{
              background: 'rgba(255, 255, 255, 0.1)',
              color: 'white',
              border: '2px solid rgba(255, 255, 255, 0.3)',
              padding: '15px 30px',
              borderRadius: '25px',
              fontSize: '1.1rem',
              fontWeight: '600',
              textDecoration: 'none',
              transition: 'all 0.3s ease',
              display: 'inline-block'
            }}
          >
            🏟️ Explore Teams
          </a>
        </div>

        {/* Key Features */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
          gap: '20px', 
          marginTop: '60px' 
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', marginBottom: '10px' }}>⭐</div>
            <h3>Emerging Players</h3>
            <p>Spot breakout stars before anyone else with our AI analysis</p>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', marginBottom: '10px' }}>🎯</div>
            <h3>54-Factor Analysis</h3>
            <p>Deep opponent-specific breakdowns beyond traditional stats</p>
          </div>
          <div style={{ textAlign: 'center' }}>
