function App() {
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [user, setUser] = useState(null);

  // Check for returning users (using state instead of localStorage for now)
  useEffect(() => {
    // Mock user check - replace with real auth later
    const mockUser = {
      username: 'demo_user',
      email: 'demo@cfbanalytics.com',
      homeTeam: 'Alabama',
      subscription: 'trial'
    };
    // setUser(mockUser); // Uncomment this line to test with a logged-in user
  }, []);

  const handleSubscription = (plan) => {
    console.log('Selected plan:', plan);
    setShowSubscriptionModal(false);
    // Handle subscription logic here
  };

  const handleRegistration = (userData) => {
    setUser(userData);
    console.log('User registered:', userData);
  };

  const handleLogin = () => {
    // For now, just show the registration modal
    // In real app, this would show login form
    window.location.href = '/register';
  };

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <Router>
      <div className="App">
        <Navbar
          user={user}
          onLogin={handleLogin}
          onLogout={handleLogout}
        />

        <Routes>
          {/* Homepage */}
          <Route
            path="/"
            element={
              <HomePage
                user={user}
                onSubscriptionClick={() => setShowSubscriptionModal(true)}
              />
            }
          />

          {/* Registration */}
          <Route
            path="/register"
            element={
              <RegistrationSystem
                onRegistrationComplete={handleRegistration}
              />
            }
          />

          {/* Conference Pages */}
          <Route path="/conferences" element={<ConferencePage />} />
          <Route path="/conference/:conferenceId" element={<ConferencePage />} />
          <Route path="/sec" element={<ConferencePage conference="SEC" />} />
          <Route path="/big-ten" element={<ConferencePage conference="Big Ten" />} />
          <Route path="/big-12" element={<ConferencePage conference="Big 12" />} />
          <Route path="/acc" element={<ConferencePage conference="ACC" />} />
          <Route path="/pac-12" element={<ConferencePage conference="Pac-12" />} />

          {/* Team Pages */}
          <Route path="/team/:teamId" element={<TeamPage />} />

          {/* Game Analysis */}
          <Route path="/game/:gameId" element={<GamePage />} />
          <Route path="/games" element={<GamePage />} />

          {/* Community Features */}
          <Route
            path="/chat"
            element={
              <CommunityChat
                user={user}
                onSubscriptionClick={() => setShowSubscriptionModal(true)}
              />
            }
          />

          {/* Trivia Game */}
          <Route
            path="/trivia"
            element={
              <TriviaGame
                user={user}
                onSubscriptionClick={() => setShowSubscriptionModal(true)}
              />
            }
          />

          {/* Predictions (Premium) */}
          <Route
            path="/predictions"
            element={
              <HomePage
                user={user}
                onSubscriptionClick={() => setShowSubscriptionModal(true)}
              />
            }
          />

          {/* Catch-all redirect to home */}
          <Route path="*" element={
            <HomePage
              user={user}
              onSubscriptionClick={() => setShowSubscriptionModal(true)}
            />
          } />
        </Routes>

        {/* Subscription Modal */}
        {showSubscriptionModal && (
          <SubscriptionModal
            isOpen={showSubscriptionModal}
            onClose={() => setShowSubscriptionModal(false)}
            onSuccess={() => {
              setShowSubscriptionModal(false);
              // Handle successful subscription
              console.log('Subscription successful!');
            }}
          />
        )}
      </div>
    </Router>
  );
}

export default App;
